import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http'; // Import HttpHeaders
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class NotificationService {
  private jwtToken: string = ''; 

  constructor(private http: HttpClient) {}

  getNotifications(headers: HttpHeaders): Observable<any[]> { // Add headers parameter
    return this.http.get<any[]>('/user/notification', { headers }); // Pass headers in the request
  }

  deleteNotification(notificationId: string, headers: HttpHeaders): Observable<any> { // Add headers parameter
    return this.http.delete(`/user/notification/${notificationId}`, { headers }); // Pass headers in the request
  }
}
