import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import swal from 'sweetalert2';

@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.css']
})
export class PostComponent implements OnInit {
  authenticated = false;
  selectedFile: File | null = null;
  jwtToken: string | null = null;
  post = {
    content: '',
    image:''
  };

  constructor(private formbuilder: FormBuilder, private http: HttpClient, private router:Router) {}

  ngOnInit(): void {
    this.jwtToken = localStorage.getItem('token')

    if(!this.jwtToken){
      this.router.navigate(['/login']); 
    }
  }

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
    console.log("Selected File:", this.selectedFile);
  }

  submit(): void {
    if (!this.jwtToken) {
      swal.fire('Error!', 'Please log in to create a post.', 'error');
      return;
    }

    const content = this.post.content;
    const image = this.selectedFile;

    console.log("Content:", content);
    console.log("Image:", image);

    if (!content && !image) {
      swal.fire('Error!', 'Please provide either post content or an image.', 'error');
      return;
    }

    const formData = new FormData();
    formData.append('content', content);

    if (image) {
      formData.append('image', image, image.name);
    }
    console.log("FormData:", formData);

    const headers = new HttpHeaders({
      Authorization: `Bearer ${this.jwtToken}`
    });

    console.log('Form Data Content:', formData);
    console.log('Form Data Image:', formData.get('image'));

    this.http.post<any>('http://localhost:9200/user/post', formData, {
      headers: headers
    }).subscribe(
      (response) => {
        console.log(headers);
        console.log('Form Data Content:', formData.get('content'));
        console.log('Form Data Image:', formData.get('image'));
        this.post.content = ''; // Reset content
        this.selectedFile = null; // Reset selectedFile
        swal.fire({
          title: 'Success!',
          text: response.message,
          icon: 'success',
          timer: 1500,
          showConfirmButton: false
        });
        this.router.navigate(['/']); 
      },
      (error) => {
        console.error(error);
      }
    );
  }

}
