import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  message: string = "";
  token: string | null = null;
  authenticated = false;
  posts: any[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.token = localStorage.getItem('token');

    if (this.token) {
      this.authenticated = true;
      const fn = localStorage.getItem('firstname');
      const ln = localStorage.getItem('lastname');
      this.message = `${fn} ${ln}'s newsfeed`; // Improved string formatting
      this.fetchPosts();
    }
  }

  fetchPosts(): void {
    const headers= new HttpHeaders().set('Authorization', `Bearer ${this.token}`);
    this.http.get<any>('http://localhost:9000/user/post', { headers: headers }).subscribe(
      (posts) => {
        this.posts = posts;
      },
      (error) => {
        console.error('Error fetching posts:', error);
      }
    );
  }
}
<div class="container" *ngIf="authenticated">
  <h1 class="header">{{ message }}</h1>
  <div class="post-container" *ngIf="posts.length > 0">
    <div *ngFor="let post of posts" class="post-card">
      <hr class="post-divider" />
      <h4><b>Username: {{ post.username }}</b></h4>
      <p>{{ post.content }}</p>
      <img *ngIf="post.imageUrl" [src]="post.imageUrl" alt="Post Image" class="post-image" />
      <hr class="post-divider" />
    </div>
  </div>
</div>
