import { Component } from '@angular/core';

@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.css']
})
export class PostComponent {

}
<h2>Create a Post</h2>

<form (ngSubmit)="submitPost()">
  <div>
    <label for="content">Content:</label>
    <textarea id="content" [(ngModel)]="post.content" name="content" placeholder="Write your post here..." required></textarea>
    <input type="file"  name="image" class="a" (change)="onFileSelected($event)" />
  </div>
  <button type="submit">Submit</button>
</form>
