import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import swal from 'sweetalert2';
import { Emitters } from '../emitters/emitter';

@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.css']
})
export class PostComponent implements OnInit{
  form:FormGroup
  authenticated=false;
  selectedFile: File | null = null;

  constructor(private formbuilder:FormBuilder, private http:HttpClient, private router:Router){}
  ngOnInit(): void {
    Emitters.authEmitter.subscribe((auth: boolean) => {
      this.authenticated = auth;
    });

    this.form = this.formbuilder.group({
      content: ['', Validators.required],
      image: [''],
    });
  }

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  submit(): void {
    if (!this.authenticated) {
      swal.fire('Error!', 'Please log in to create a post.', 'error');
      return;
    }

    const content = this.form.get('content')?.value;
    const image = this.selectedFile;

    if (!content && !image) {
      swal.fire('Error!', 'Please provide either post content or an image.', 'error');
      return;
    }

    const post = this.form.getRawValue();

    if (image) {
      post.image = this.selectedFile;

      this.http.post<any>('http://localhost:9000/user/post', post).subscribe(
        (response) => {
          Emitters.authEmitter.emit(true);
        this.message = `Hi ${res.firstname} ${res.lastname}`;
          console.log(response);
          this.form.reset();
          this.selectedFile = null;
        },
        (error) => {
          console.error(error);
        }
      );
    } else {
      this.http.post('http://localhost:9000/user/post', post).subscribe(
        (response: any) => {
          console.log(response);
          this.form.reset();
        }
      );
    }
  }
}
