import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  message: string = "";
  token: string | null = null;
  authenticated = false;
  posts: any[] = [];

  constructor(private http: HttpClient, private router:Router) {}

  ngOnInit(): void {
    this.token = localStorage.getItem('token');

    if (this.token) {
      this.authenticated = true;
      const fn = localStorage.getItem('firstname');
      const ln = localStorage.getItem('lastname');
      this.message = `${fn} ${ln}'s newsfeed`;
      this.fetchPosts();
    }
    else{
      this.router.navigate(['/login']); 
    }
  }

  fetchPosts(): void {
    const headers= new HttpHeaders().set('Authorization', `Bearer ${this.token}`);
    this.http.get<any>('http://localhost:9200/user/post', { headers: headers }).subscribe(
      (posts) => {
        this.posts = posts;
        console.log(this.posts)
      },
      (error) => {
        console.error('Error fetching posts:', error);
      }
    );

  }
}
<div class="navbar-container">
  <app-navbar></app-navbar>
</div>
<div class="content-container">
  <h1 *ngIf="!authenticated">Welcome to Demo LinkedIn</h1><br>
  <div class="imgcn" *ngIf="!authenticated">
    <img src="../../assets/images/vector-illustration-virtual-business-assistant-260nw-1328365757.jpg">
  </div>
  <div class="container" *ngIf="authenticated">
    <h1 class="header">{{ message }}</h1>
    <div class="post-container" *ngIf="posts.length > 0">
      <div *ngFor="let post of posts" class="post-card">
        <hr class="post-divider" />
        <h4><b>Username: {{ post.name }}</b></h4>
        <p>{{ post.content }}</p>
        <img *ngIf="post.imageUrl" [src]="post.imageUrl" alt="Post Image" class="post-image" />
        <hr class="post-divider" />
      </div>
    </div>
  </div>
</div>
