import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import swal from 'sweetalert2';

@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.css']
})
export class PostComponent implements OnInit{
  form:FormGroup

  constructor(private formbuilder:FormBuilder, private http:HttpClient, private router:Router){}
  ngOnInit():void{
    this.form = this.formbuilder.group({
      content: [''],
      image: [''],
    });
  }
loggedInUser: any;

selectedFile: File | null = null;
  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }
  submit(): void {
    const content = this.form.get('content')?.value;
    const image = this.selectedFile;

    if (!content && !image) {
      swal.fire('Error!', 'Please provide either post content or an image.', 'error');
      return;
    }

    const post = this.form.getRawValue();

    this.http.post("http://localhost:9000/user/login", user, {
      withCredentials: true
    })
    .subscribe(
      (response: any) => {
        const successMessage = response.message; 
        swal.fire({
          title: 'Success!',
          text: successMessage,
          icon: 'success',
          timer: 1500,
          showConfirmButton: false
        });
        this.router.navigate(['/']); 
      },
      (err: any) => {
        console.error("Login error:", err);
        swal.fire('Error!', err.error?.message, 'error');
      });
    }

  submitPost() {
    if (this.selectedFile) {
      const formData = new FormData();
      formData.append('content', this.post.content);
      formData.append('image', this.selectedFile, this.selectedFile.name);

      this.http.post<any>('http://localhost:8080/posts', formData).subscribe(
        (response) => {
          console.log(response);
          this.post.content = '';
          this.selectedFile= null;
        },
        (error) => {
          console.error(error);
        }
      );
    } else {
      this.http.post('http://localhost:8080/posts', this.post)
        .subscribe((response: any) => {
          console.log(response);
          this.post.content = '';
        });
    }
  }
}

<h2>Create a Post</h2>

<form [formGroup]="form" (ngSubmit)="submit()">
  <div>
    <label for="content">Content:</label>
    <textarea id="content" [(ngModel)]="post.content" name="content" placeholder="Write your post here..." required></textarea>
    <input type="file"  name="image" class="a" (change)="onFileSelected($event)" />
  </div>
  <input type="submit" value="POST">
</form>
